import base64
evil_blob = ''
bin = base64.b64decode(evil_blob)
print(bin)
print()
output = ""
for byte in bin:
	newbyte = int(byte) ^ int(14) # 14 = XOR key
	output = output + chr(newbyte)
print(output)
output = output.replace('\x00','')
print(output)
